{{(async () => {
	var releaseType = appsmith.store.upgradeType
	await UpgradeRelease.run({
		upgradeName: "Release "+ Input4.text,
		upgradeVersion: Input4.text,
		upgradeId: Table1.selectedRow.id,
		upgradeStatus: Select1.selectedOptionValue
	}).then((res) => {
		showAlert("Selected release upgraded to " + Input4.text)
		if(Table1.selectedRowIndex != 0) {
			UpgradeRelease.run({
				upgradeName: "Release "+ Input5.text,
				upgradeVersion: Input5.text,
				upgradeId: Table1.tableData[0].id,
				upgradeStatus: Table1.tableData[0].status
			}).then(() => {
				showAlert("New release " + Table1.tableData[0].version + " upgraded to " + Input5.text)
			}).catch(() => {
				showAlert("There was an error while ")
			})
		}
	}).catch(() => {
		showAlert("There was an error while upgrading")
	})
	if(releaseType == 'adhoc') {
		await createAdhocRelease.run({
			adhocVersion: Table1.selectedRow.version,
			adhocName: Table1.selectedRow.name,
			adhocTagDate: Table1.selectedRow.tag_date
		}).then(() => {
			showAlert("Adhoc release created")
		}).catch(() => {
			showAlert("Error creating adhoc release")
		})
	}
	upgradeIntimationOnSlack.run().then(() => {
		showAlert("all updates were sucessfull")
		closeModal("UpgradeReleaseModal")
		fetch_releases.run()
	}).catch(() => {
		showAlert("Error notifying release channel on this")
	})
})()}}
